outputTemplate.template:
export interface Person {
  id: string;
  name: string;
  email: string;
  jobTitle?: string;
}

export const people: Person[] = [
{{#each results}}
  {
    id: '{{person}}',
    name: '{{name}}',
    email: '{{email}}'{{#if jobTitle}},
    jobTitle: '{{jobTitle}}'{{/if}}
  }{{#unless @last}},{{/unless}}
{{/each}}
];
